import { useState, useEffect } from "react";
import { Redirect, useParams } from "react-router-dom";

export default function EditPost() {
  const { id } = useParams();
  const [title, setTitle] = useState('');
  const [summary, setSummary] = useState('');
  const [content, setContent] = useState('');
  const [editRedirect, setEditRedirect] = useState(false);
  const [deleteRedirect, setDeleteRedirect] = useState(false);

  useEffect(() => {
    fetch(`http://localhost:4000/post/${id}`)
      .then(response => {
        if (response.ok) {
          return response.json();
        } else {
          throw new Error('Failed to fetch post');
        }
      })
      .then(postInfo => {
        setTitle(postInfo.title);
        setSummary(postInfo.summary);
        setContent(postInfo.content);
      })
      .catch(error => {
        console.error(error);
      });
  }, [id]);

  async function updatePost(ev) {
    ev.preventDefault();
    // Update post logic
    setEditRedirect(true); // Redirect to post page after editing
  }

  async function deletePost() {
    const response = await fetch(`http://localhost:4000/post/${id}`, {
      method: 'DELETE',
      credentials: 'include',
    });
    if (response.ok) {
      setDeleteRedirect(true); // Redirect to homepage after deletion
    }
  }

  if (editRedirect) {
    return <Redirect to={`/post/${id}`} />;
  }

  if (deleteRedirect) {
    return <Redirect to="/" />;
  }

  return (
    <form onSubmit={updatePost}>
      <input type="text" value={title} onChange={ev => setTitle(ev.target.value)} />
      <input type="text" value={summary} onChange={ev => setSummary(ev.target.value)} />
      <textarea value={content} onChange={ev => setContent(ev.target.value)} />
      <button type="submit">Update Post</button>
      <button type="button" onClick={deletePost}>Delete Post</button>
    </form>
  );
}
